import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, ListGroup, InputGroup } from 'react-bootstrap';

const EditTimersModal = ({ show, handleClose, quiz }) => {
  const [timers, setTimers] = useState({});
  const [globalTimer, setGlobalTimer] = useState('');

  useEffect(() => {
    if (quiz) {
      const savedTimers = localStorage.getItem(`quiz_timers_${quiz.id}`);
      if (savedTimers) {
        setTimers(JSON.parse(savedTimers));
      } else {
        const initialTimers = quiz.questions.reduce((acc, q) => {
          acc[q.id] = q.timer || '';
          return acc;
        }, {});
        setTimers(initialTimers);
      }
    }
  }, [quiz]);

  const handleTimerChange = (questionId, value) => {
    setTimers({ ...timers, [questionId]: value });
  };

  const handleGlobalTimerChange = (e) => {
    setGlobalTimer(e.target.value);
  };

  const applyGlobalTimer = () => {
    const newTimers = {};
    for (const questionId in timers) {
      newTimers[questionId] = globalTimer;
    }
    setTimers(newTimers);
  };

  const handleSave = () => {
    localStorage.setItem(`quiz_timers_${quiz.id}`, JSON.stringify(timers));
    handleClose();
  };

  return (
    <Modal show={show} onHide={handleClose} size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Edit Timers for {quiz?.title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <InputGroup className="mb-3">
          <Form.Control
            type="number"
            placeholder="Set timer for all questions"
            value={globalTimer}
            onChange={handleGlobalTimerChange}
          />
          <Button variant="outline-secondary" onClick={applyGlobalTimer}>
            Apply to All
          </Button>
        </InputGroup>
        <ListGroup>
          {quiz?.questions.map((question) => (
            <ListGroup.Item key={question.id}>
              <div className="d-flex justify-content-between align-items-center">
                <span>{question.text}</span>
                <InputGroup style={{ width: '150px' }}>
                  <Form.Control
                    type="number"
                    placeholder="Seconds"
                    value={timers[question.id] || ''}
                    onChange={(e) => handleTimerChange(question.id, e.target.value)}
                  />
                  <InputGroup.Text>sec</InputGroup.Text>
                </InputGroup>
              </div>
            </ListGroup.Item>
          ))}
        </ListGroup>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleSave}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditTimersModal;

