import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { database } from '../services/firebase';
import { ref, push } from 'firebase/database';
import { validateQuizJson } from '../utils/validation';

const UploadQuizModal = ({ show, handleClose }) => {
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = (e) => {
    e.preventDefault();
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const quizData = JSON.parse(event.target.result);
          const { valid, error } = validateQuizJson(quizData);

          if (!valid) {
            alert(`Invalid JSON file: ${error}`);
            return;
          }

          quizData.questions.forEach(question => {
            if (!question.timer) {
              question.timer = 10;
            }
          });

          const quizzesRef = ref(database, 'quizzes');
          push(quizzesRef, quizData);
          handleClose();
        } catch (error) {
          console.error("Error parsing JSON file:", error);
          alert("Invalid JSON file.");
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Upload Quiz</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleUpload}>
          <Form.Group controlId="formFile" className="mb-3">
            <Form.Label>Select a JSON file</Form.Label>
            <Form.Control type="file" accept=".json" onChange={handleFileChange} required />
          </Form.Group>
          <Button variant="primary" type="submit">
            Upload
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
};

export default UploadQuizModal;

