import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Button } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, update } from 'firebase/database';
import { database } from '../services/firebase';
import Avatar from '../components/Avatar.jsx';

const TeacherWaitingRoomPage = () => {
  console.log('TeacherWaitingRoomPage rendered');
  const [players, setPlayers] = useState([]);
  const [quiz, setQuiz] = useState(null);
  const [gameSession, setGameSession] = useState(null);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem('isAdmin');
    if (!isAdmin) {
      navigate('/');
    }

    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    const unsubscribeGameSession = onValue(gameSessionRef, (snapshot) => {
      console.log('onValue triggered');
      const sessionData = snapshot.val();
      console.log('sessionData:', sessionData);
      setGameSession(sessionData);

      if (sessionData && sessionData.quizId) {
        console.log('quizId found:', sessionData.quizId);
        const quizRef = ref(database, `quizzes/${sessionData.quizId}`);
        get(quizRef).then((quizSnapshot) => {
          console.log('quizSnapshot exists:', quizSnapshot.exists());
          if (quizSnapshot.exists()) {
            setQuiz({ id: quizSnapshot.key, ...quizSnapshot.val() });
          }
        });
      }

      if (sessionData && sessionData.players) {
        console.log('players found:', sessionData.players);
        const playerList = Object.keys(sessionData.players).map(key => ({ id: key, ...sessionData.players[key] }));
        setPlayers(playerList);
      } else {
        console.log('no players found');
        setPlayers([]);
      }
    });

    return () => {
      console.log('unsubscribe');
      unsubscribeGameSession();
    };
  }, [gameSessionId]);

  const handleStartQuiz = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'in-progress', currentQuestion: 0 });
    navigate(`/teacher-quiz/${gameSessionId}`);
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md="8">
          <h1 className="text-center mb-4">Waiting Room</h1>
          {quiz && <h3 className="text-center mb-4">{quiz.title}</h3>}
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h3>Players Joined: {players.length}</h3>
            <Button variant="success" size="lg" onClick={handleStartQuiz}>Start Quiz</Button>
          </div>
          <ListGroup>
            {players.map((player) => (
              <ListGroup.Item key={player.id} className="d-flex align-items-center">
                <Avatar name={player.name} />
                <span className="ms-3">{player.name.length > 15 ? `${player.name.substring(0, 15)}...` : player.name}</span>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
    </Container>
  );
};

export default TeacherWaitingRoomPage;

