import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, push, update } from 'firebase/database';
import { database } from '../services/firebase';

const StudentQuizPage = () => {
  const [quiz, setQuiz] = useState(null);
  const [gameSession, setGameSession] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const { gameSessionId } = useParams();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    const unsubscribeGameSession = onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      setGameSession(sessionData);

      if (sessionData && sessionData.status === 'finished') {
        navigate(`/student-results/${gameSessionId}`);
      }

      if (sessionData && sessionData.status === 'show_results') {
        setShowFeedback(true);

        setTimeout(() => {
          setShowFeedback(false);
        }, 5000);

        const playerId = localStorage.getItem('playerId');
        const playerRef = ref(database, `game_sessions/${gameSessionId}/players/${playerId}`);
        get(playerRef).then((playerSnapshot) => {
          if (playerSnapshot.exists()) {
            const playerData = playerSnapshot.val();
            const playerAnswer = sessionData.answers && sessionData.answers[sessionData.currentQuestion] ? Object.values(sessionData.answers[sessionData.currentQuestion]).find(a => a.playerId === playerId) : null;
            const isCorrect = playerAnswer && quiz.questions[sessionData.currentQuestion].correctAnswer === playerAnswer.answer;

            if (isCorrect) {
              update(playerRef, { score: (playerData.score || 0) + 1 });
            }
          }
        });
      } else {
        setShowFeedback(false);
      }

      if (sessionData && sessionData.quizId) {
        const quizRef = ref(database, `quizzes/${sessionData.quizId}`);
        get(quizRef).then((quizSnapshot) => {
          if (quizSnapshot.exists()) {
            const quizData = { id: quizSnapshot.key, ...quizSnapshot.val() };
            setQuiz(quizData);
            if (sessionData.currentQuestion !== undefined) {
              setCurrentQuestion(quizData.questions[sessionData.currentQuestion]);
            }
          }
        });
      }
    });

    return () => {
      unsubscribeGameSession();
    };
  }, [gameSessionId]);

  const handleAnswerSubmit = (answer) => {
    if (gameSession && currentQuestion) {
      const playerId = localStorage.getItem('playerId');
      const answersRef = ref(database, `game_sessions/${gameSessionId}/answers/${gameSession.currentQuestion}`);
      push(answersRef, { answer, playerId });
    }
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <Row>
        <Col>
          {showFeedback ? (
            <div>
              {(() => {
                const playerId = localStorage.getItem('playerId');
                const playerAnswer = gameSession.answers && gameSession.answers[gameSession.currentQuestion] ? Object.values(gameSession.answers[gameSession.currentQuestion]).find(a => a.playerId === playerId) : null;
                const isCorrect = playerAnswer && playerAnswer.answer === currentQuestion.correctAnswer;
                const playerScore = gameSession.players && gameSession.players[playerId] ? gameSession.players[playerId].score || 0 : 0;
                return (
                  <div>
                    <h1>{isCorrect ? 'Correct!' : 'Incorrect'}</h1>
                    <p>The correct answer is: {currentQuestion.correctAnswer}</p>
                    <p>Your score: {playerScore}</p>
                  </div>
                );
              })()}
            </div>
          ) : (
            currentQuestion && currentQuestion.options && (
              <div className="d-grid gap-2">
                {currentQuestion.options.map((option, index) => (
                  <Button key={index} variant="primary" size="lg" onClick={() => handleAnswerSubmit(option)}>
                    {option}
                  </Button>
                ))}
              </div>
            )
          )}
        </Col>
      </Row>
    </Container>
  );
};

export default StudentQuizPage;

