import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Button } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue } from 'firebase/database';
import { database } from '../services/firebase';

import './StudentResultsPage.css';

const StudentResultsPage = () => {
  const [players, setPlayers] = useState([]);
  const [player, setPlayer] = useState(null);
  const [rank, setRank] = useState(0);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (sessionData && sessionData.players) {
        const playerList = Object.entries(sessionData.players).map(([id, data]) => ({ id, ...data })).sort((a, b) => (b.score || 0) - (a.score || 0));
        setPlayers(playerList);

        const playerId = localStorage.getItem('playerId');
        const currentPlayer = playerList.find(p => p.id === playerId);
        if (currentPlayer) {
          setPlayer(currentPlayer);
          setRank(playerList.findIndex(p => p.id === playerId) + 1);
        }
      }
    });
  }, [gameSessionId]);

  return (
    <Container className="mt-5 student-results-page">
      <Row className="justify-content-md-center">
        <Col md="8">
          <h1 className="text-center mb-4">Results</h1>
          {player && (
            <div className="rank-card">
              <h2>Your Score: {player.score || 0}</h2>
              <h3>Your Rank: {rank}</h3>
            </div>
          )}
          <hr />
          <h2>Top 5 Players</h2>
          <ListGroup>
            {players.slice(0, 5).map((p, index) => (
              <ListGroup.Item key={p.id}>
