import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Button, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { database } from '../services/firebase';
import { ref, onValue, remove, push, update } from 'firebase/database';
import UploadQuizModal from '../components/UploadQuizModal.jsx';
import EditTimersModal from '../components/EditTimersModal.jsx';

const TeacherLobbyPage = () => {
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [quizzes, setQuizzes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const quizzesRef = ref(database, 'quizzes');
    onValue(quizzesRef, (snapshot) => {
      const data = snapshot.val();
      const quizList = data ? Object.keys(data).map(key => ({ id: key, ...data[key] })) : [];
      setQuizzes(quizList);
    });
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      const quizRef = ref(database, `quizzes/${id}`);
      remove(quizRef);
    }
  };

  const handleEditTimers = (quiz) => {
    setSelectedQuiz(quiz);
    setShowEditModal(true);
  };

  const createMockQuiz = () => {
    const mockQuiz = {
      title: 'Mock Quiz for Testing',
      questions: [
        {
          text: 'What is 2 + 2?',
          options: ['3', '4', '5', '6'],
          correctAnswer: '4'
        },
        {
          text: 'What is the capital of France?',
          options: ['London', 'Berlin', 'Paris', 'Madrid'],
          correctAnswer: 'Paris'
        }
      ]
    };
    const quizzesRef = ref(database, 'quizzes');
    push(quizzesRef, mockQuiz);
  };

  const handleStartQuiz = (quizId) => {
    const gameSessionRef = ref(database, 'game_sessions/active_session');
    update(gameSessionRef, {
      quizId: quizId,
      status: 'waiting',
      // Explicitly clear old data from previous session
      answers: null,
      currentQuestion: null
    });
    navigate(`/teacher-wait/active_session`);
  };

  const filteredQuizzes = quizzes.filter(quiz =>
    quiz.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container className="mt-5">
      <Row className="justify-content-md-center">
        <Col md="8">
          <h1 className="text-center mb-4">Teacher Lobby</h1>
          <div className="d-flex justify-content-end mb-3">
            <Button variant="primary" onClick={() => setShowUploadModal(true)}>Upload Quiz</Button>
            <Button variant="info" onClick={createMockQuiz}>Create Mock Quiz</Button>
          </div>
          <Form.Group className="mb-3">
            <Form.Control
              type="text"
              placeholder="Search quizzes..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </Form.Group>
          <ListGroup>
            {filteredQuizzes.map((quiz) => (
              <ListGroup.Item key={quiz.id} className="d-flex justify-content-between align-items-center">
                {quiz.title}
                <div>
                  <Button variant="secondary" size="sm" className="me-2" onClick={() => handleEditTimers(quiz)}>Edit Timers</Button>
                  <Button variant="danger" size="sm" className="me-2" onClick={() => handleDelete(quiz.id)}>Delete</Button>
                  <Button variant="success" size="sm" onClick={() => handleStartQuiz(quiz.id)}>Start Quiz</Button>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        </Col>
      </Row>
      <UploadQuizModal show={showUploadModal} handleClose={() => setShowUploadModal(false)} />
      {selectedQuiz && <EditTimersModal show={showEditModal} handleClose={() => setShowEditModal(false)} quiz={selectedQuiz} />}
    </Container>
  );
};

export default TeacherLobbyPage;

