import { getDatabase } from "firebase/database";
import { getAuth } from "firebase/auth";

const firebaseConfig = {
  apiKey: import.meta.env.VITE_FIREBASE_API_KEY,
  authDomain: import.meta.env.VITE_FIREBASE_AUTH_DOMAIN,
  databaseURL: import.meta.env.VITE_FIREBASE_DATABASE_URL,
  projectId: import.meta.env.VITE_FIREBASE_PROJECT_ID,
  storageBucket: import.meta.env.VITE_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: import.meta.env.VITE_FIREBASE_MESSAGING_SENDER_ID,
  appId: import.meta.env.VITE_FIREBASE_APP_ID,
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const database = getDatabase(app);
const auth = getAuth(app);

export { database, auth };

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Form, Container, Row, Col } from 'react-bootstrap';
import AdminLoginModal from '../components/AdminLoginModal.jsx';

import { ref, push } from 'firebase/database';
import { database } from '../services/firebase';

const LoginPage = () => {
  const [name, setName] = useState('');
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const navigate = useNavigate();

  const handleStudentJoin = (e) => {
    e.preventDefault();
    if (name.trim()) {
      const playersRef = ref(database, 'game_sessions/active_session/players');
      const newPlayerRef = push(playersRef, { name: name.trim(), score: 0 });
      localStorage.setItem('playerId', newPlayerRef.key);
      navigate('/student-wait/active_session');
    }
  };

  return (
    <Container className="d-flex align-items-center justify-content-center vh-100">
      <Row>
        <Col md={12} className="text-center">
          <h1 className="mb-4">Join Game</h1>
          <Form onSubmit={handleStudentJoin} className="mx-auto" style={{ maxWidth: '400px' }}>
            <Form.Group className="mb-3" controlId="formStudentName">
              <Form.Control
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit" className="w-100">
              Join
            </Button>
          </Form>
          <div className="text-center mt-3">
            <Button variant="link" onClick={() => setShowAdminLogin(true)}>
              Admin Login
            </Button>
          </div>
        </Col>
      </Row>
      <AdminLoginModal show={showAdminLogin} handleClose={() => setShowAdminLogin(false)} />
