import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, set } from 'firebase/database';
import { database } from '../services/firebase';
import AdminLoginModal from '../components/AdminLoginModal.jsx';

import {
  Container,
  Box,
  Paper,
  TextField,
  Button,
  Typography,
  Avatar,
  Link
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';

const LoginPage = () => {
  const [name, setName] = useState('');
  const [showAdminLogin, setShowAdminLogin] = useState(false);
  const navigate = useNavigate();

  // WORKAROUND: Using set with a client-side generated ID instead of push
  const handleStudentJoin = (e) => {
    e.preventDefault();
    if (name.trim()) {
      // Generate a unique ID on the client
      const uniqueId = Date.now() + '-' + Math.random().toString(36).substr(2, 9);
      const playerRef = ref(database, `game_sessions/active_session/players/${uniqueId}`);

      set(playerRef, { name: name.trim(), score: 0 });

      localStorage.setItem('playerId', uniqueId);
      navigate('/student-wait/active_session');
    }
  };

  return (
    <Container
      component="main"
      maxWidth="xs"
      sx={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
      }}
    >
      <Paper
        elevation={3}
        sx={{
          padding: 4,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '100%',
        }}
      >
        <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
          <SchoolIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          Join Game
        </Typography>
        <Box
          component="form"
          onSubmit={handleStudentJoin}
          sx={{ mt: 1, width: '100%' }}
        >
          <TextField
            margin="normal"
            required
            fullWidth
            id="name"
            label="Enter your name"
            name="name"
            autoComplete="name"
            autoFocus
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt: 3, mb: 2 }}
          >
            Join
          </Button>
          <Box textAlign="center">
            <Link component="button" variant="body2" onClick={() => setShowAdminLogin(true)}>
              Admin Login
            </Link>
          </Box>
        </Box>
      </Paper>
      <AdminLoginModal show={showAdminLogin} handleClose={() => setShowAdminLogin(false)} />
    </Container>
  );
};

export default LoginPage;

