            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
          />
          <Box>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => setShowUploadModal(true)}
              sx={{ mr: 2 }}
            >
              Upload Quiz
            </Button>
            <Button
              variant="outlined"
              onClick={createMockQuiz}
            >
              Create Mock Quiz
            </Button>
          </Box>
        </Box>
        <Grid container spacing={3}>
          {filteredQuizzes.map((quiz) => (
            <Grid item key={quiz.id} xs={12} sm={6} md={4}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {quiz.title}
                  </Typography>
                  <Typography>
                    {quiz.questions.length} questions
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" startIcon={<PlayArrow />} color="primary" onClick={() => handleStartQuiz(quiz.id)}>
                    Start
                  </Button>
                  <Button size="small" startIcon={<Edit />} onClick={() => handleEditTimers(quiz)}>
                    Edit Timers
                  </Button>
                  <Button size="small" startIcon={<Delete />} color="error" onClick={() => handleDelete(quiz.id)}>
                    Delete
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
      {/* Note: These modals will appear unstyled until they are also redesigned. */}
      <UploadQuizModal show={showUploadModal} handleClose={() => setShowUploadModal(false)} />
      {selectedQuiz && <EditTimersModal show={showEditModal} handleClose={() => setShowEditModal(false)} quiz={selectedQuiz} />}
    </Box>
  );
};

export default TeacherLobbyPage;

