import React, { useState } from 'react';
import { database } from '../services/firebase';
import { ref, push } from 'firebase/database';
import { validateQuizJson } from '../utils/validation';

import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Box,
  Typography
} from '@mui/material';
import { UploadFile } from '@mui/icons-material';

const UploadQuizModal = ({ show, handleClose }) => {
  const [file, setFile] = useState(null);
  const [fileName, setFileName] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setFileName(selectedFile.name);
    }
  };

  const handleUpload = (e) => {
    e.preventDefault();
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const quizData = JSON.parse(event.target.result);
          const { valid, error } = validateQuizJson(quizData);

          if (!valid) {
            alert(`Invalid JSON file: ${error}`);
            return;
          }

          quizData.questions.forEach(question => {
            if (!question.timer) {
              question.timer = 10;
            }
          });

          const quizzesRef = ref(database, 'quizzes');
          push(quizzesRef, quizData);
          handleClose();
          setFile(null);
          setFileName('');
        } catch (error) {
          console.error("Error parsing JSON file:", error);
          alert("Invalid JSON file.");
        }
      };
      reader.readAsText(file);
    }
  };

  const handleCancel = () => {
    handleClose();
    setFile(null);
    setFileName('');
  };

  return (
    <Dialog open={show} onClose={handleCancel} fullWidth maxWidth="sm">
      <DialogTitle>Upload New Quiz</DialogTitle>
      <Box component="form" onSubmit={handleUpload}>
        <DialogContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
            <Button
              variant="outlined"
              component="label"
              startIcon={<UploadFile />}
            >
              Choose File
              <input
                type="file"
                hidden
                accept=".json"
                onChange={handleFileChange}
              />
            </Button>
            {fileName && (
              <Typography variant="body1" sx={{ ml: 2 }}>
                {fileName}
              </Typography>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCancel}>Cancel</Button>
          <Button type="submit" variant="contained" disabled={!file}>
            Upload
          </Button>
        </DialogActions>
      </Box>
    </Dialog>
  );
};

export default UploadQuizModal;

