import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Box,
  InputAdornment,
  Typography
} from '@mui/material';

const EditTimersModal = ({ show, handleClose, quiz }) => {
  const [timers, setTimers] = useState({});
  const [globalTimer, setGlobalTimer] = useState('');

  useEffect(() => {
    if (quiz) {
      const savedTimers = localStorage.getItem(`quiz_timers_${quiz.id}`);
      if (savedTimers) {
        setTimers(JSON.parse(savedTimers));
      } else {
        const initialTimers = quiz.questions.reduce((acc, q, index) => {
          acc[index] = q.timer || ''; // Use index as key
          return acc;
        }, {});
        setTimers(initialTimers);
      }
    }
  }, [quiz]);

  const handleTimerChange = (questionIndex, value) => {
    setTimers({ ...timers, [questionIndex]: value });
  };

  const handleGlobalTimerChange = (e) => {
    setGlobalTimer(e.target.value);
  };

  const applyGlobalTimer = () => {
    if (globalTimer && !isNaN(globalTimer)) {
        const newTimers = {};
        quiz.questions.forEach((q, index) => {
            newTimers[index] = globalTimer;
        });
        setTimers(newTimers);
    }
  };

  const handleSave = () => {
    // Here you would typically save to a backend. For now, we save to localStorage.
    // Note: The logic to apply these timers during the quiz needs to be implemented.
    localStorage.setItem(`quiz_timers_${quiz.id}`, JSON.stringify(timers));
    handleClose();
  };

  // The original component had a bug where question.id was used as a key, 
  // but questions in the JSON don't have IDs. Using the index is more robust.
  return (
    <Dialog open={show} onClose={handleClose} fullWidth maxWidth="md">
      <DialogTitle>
        Edit Timers for <Typography component="span" variant="h6" color="primary">{quiz?.title}</Typography>
      </DialogTitle>
      <DialogContent>
        <Box sx={{ display: 'flex', alignItems: 'center', my: 2 }}>
          <TextField
            type="number"
            label="Global Timer"
            placeholder="Set for all questions"
            value={globalTimer}
            onChange={handleGlobalTimerChange}
            sx={{ mr: 2 }}
            InputProps={{
              endAdornment: <InputAdornment position="end">sec</InputAdornment>,
            }}
          />
          <Button variant="outlined" onClick={applyGlobalTimer}>
            Apply to All
          </Button>
        </Box>
        <List>
          {quiz?.questions.map((question, index) => (
            <ListItem key={index} divider>
              <ListItemText primary={question.text} />
              <TextField
                type="number"
                label="Timer"
                size="small"
                sx={{ width: '150px' }}
                value={timers[index] || ''}
                onChange={(e) => handleTimerChange(index, e.target.value)}
                InputProps={{
                  endAdornment: <InputAdornment position="end">sec</InputAdornment>,
                }}
              />
            </ListItem>
          ))}
        </List>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose}>Cancel</Button>
        <Button variant="contained" onClick={handleSave}>
          Save Changes
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default EditTimersModal;

