import React from 'react';
import { Box, Typography, LinearProgress, IconButton, Tooltip } from '@mui/material';
import { PersonRemove } from '@mui/icons-material';

const RocketRaceChart = ({ players, maxScore, onKickPlayer }) => {
  // Sort players by score descending
  const sortedPlayers = [...players].sort((a, b) => (b.score || 0) - (a.score || 0));

  return (
    <Box>
      {sortedPlayers.map(player => {
        const progress = maxScore > 0 ? ((player.score || 0) / maxScore) * 100 : 0;
        return (
          <Box key={player.id} sx={{ display: 'flex', alignItems: 'center', mb: 1.5 }}>
            <Typography variant="body2" sx={{ width: '100px', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', mr: 1 }}>
              {player.name}
            </Typography>
            <Box sx={{ width: '100%', mr: 1 }}>
              <LinearProgress variant="determinate" value={progress} />
            </Box>
            <Typography variant="body2" sx={{ width: '40px', textAlign: 'right' }}>
              {player.score || 0}
            </Typography>
            <Tooltip title="Kick Player">
              <IconButton color="error" size="small" onClick={() => onKickPlayer(player.id)} sx={{ ml: 1 }}>
                <PersonRemove />
              </IconButton>
            </Tooltip>
          </Box>
        );
      })}
    </Box>
  );
};

export default RocketRaceChart;

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, update, remove } from 'firebase/database';
import { database } from '../services/firebase';
import RocketRaceChart from '../components/RocketRaceChart.jsx';
import QuestionRenderer from '../components/QuestionRenderer.jsx';

import {
  Box,
  Grid,
  Paper,
  Typography,
  CircularProgress,
  AppBar,
  Toolbar,
  Button,
  IconButton,
  Tooltip
} from '@mui/material';
import { Pause, PlayArrow, SkipNext, StopCircle } from '@mui/icons-material';

const TeacherQuizPage = () => {
  const [quiz, setQuiz] = useState(null);
  const [gameSession, setGameSession] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answers, setAnswers] = useState({});
  const [players, setPlayers] = useState([]);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    const unsubscribeGameSession = onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      setGameSession(sessionData);

      if (sessionData?.players) {
        const playerList = Object.keys(sessionData.players).map(key => ({ id: key, ...sessionData.players[key] }));
        setPlayers(playerList);
      } else {
        setPlayers([]);
      }

      if (sessionData?.quizId && !quiz) {
        const quizRef = ref(database, `quizzes/${sessionData.quizId}`);
        get(quizRef).then((quizSnapshot) => {
          if (quizSnapshot.exists()) {
            const quizData = { id: quizSnapshot.key, ...quizSnapshot.val() };
            setQuiz(quizData);
          }
        });
      }

      if (quiz && sessionData?.currentQuestion !== undefined) {
        setCurrentQuestion(quiz.questions[sessionData.currentQuestion]);
        const answersRef = ref(database, `game_sessions/${gameSessionId}/answers/${sessionData.currentQuestion}`);
        onValue(answersRef, (answersSnapshot) => {
          const answersData = answersSnapshot.val();
          if (answersData) {
            const answerCounts = Object.values(answersData).reduce((acc, { answer }) => {
              acc[answer] = (acc[answer] || 0) + 1;
              return acc;
            }, {});
            setAnswers(answerCounts);
          } else {
            setAnswers({});
          }
        });
      }
    });

    return () => unsubscribeGameSession();
  }, [gameSessionId, quiz]);

  const handleKickPlayer = (playerId) => {
    if (window.confirm('Are you sure you want to kick this player?')) {
      const playerRef = ref(database, `game_sessions/${gameSessionId}/players/${playerId}`);
      remove(playerRef);
    }
  };

  const handleShowResults = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'show_results' });
  };

  const handleNextQuestion = () => {
    if (gameSession && quiz) {
      if (gameSession.currentQuestion < quiz.questions.length - 1) {
        const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
        update(gameSessionRef, { currentQuestion: gameSession.currentQuestion + 1, status: 'in-progress' });
      } else {
        handleEndQuiz();
      }
    }
  };

  const handleEndQuiz = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'finished' });
    navigate(`/teacher-results/${gameSessionId}`);
  };

  if (!quiz || !gameSession) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', height: '100vh' }}>
      <Box sx={{ flexGrow: 1, p: 3, mb: '64px' /* Height of bottom app bar */ }}>
        <Grid container spacing={3} sx={{ height: '100%' }}>
          {/* Left Column: Question */}
          <Grid item xs={12} md={8} sx={{ height: '100%' }}>
            <Paper elevation={3} sx={{ height: '100%', p: 2, overflowY: 'auto' }}>
              <QuestionRenderer question={currentQuestion} answers={answers} />
            </Paper>
          </Grid>

          {/* Right Column: Meta */}
          <Grid item xs={12} md={4} sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
            {currentQuestion?.passage && (
              <Paper elevation={3} sx={{ p: 2, mb: 2 }}>
                <Typography variant="h6" gutterBottom>Passage/Material</Typography>
                <Typography variant="body2">{currentQuestion.passage}</Typography>
              </Paper>
            )}
            <Paper elevation={3} sx={{ p: 2, flexGrow: 1 }}>
              <Typography variant="h6" gutterBottom>Rocket Race</Typography>
              <RocketRaceChart players={players} maxScore={quiz.questions.length} onKickPlayer={handleKickPlayer} />
            </Paper>
          </Grid>
        </Grid>
      </Box>

      {/* Bottom Control Bar */}
      <AppBar position="fixed" color="primary" sx={{ top: 'auto', bottom: 0 }}>
        <Toolbar>
          <Tooltip title="Pause Timer (Not Implemented)">
            <span>
              <IconButton color="inherit" disabled>
                <Pause />
              </IconButton>
            </span>
          </Tooltip>
          <Box sx={{ flexGrow: 1 }} />
          <Button color="inherit" onClick={handleShowResults}>Show Results</Button>
          <Button variant="contained" color="secondary" startIcon={<SkipNext />} onClick={handleNextQuestion} sx={{ ml: 2 }}>
            Next Question
          </Button>
          <Tooltip title="End Quiz Immediately">
            <IconButton color="error" sx={{ ml: 2 }} onClick={handleEndQuiz}>
              <StopCircle />
            </IconButton>
          </Tooltip>
        </Toolbar>
      </AppBar>
    </Box>
  );
};

export default TeacherQuizPage;

