import React, { useState, useEffect } from 'react';
import { Container, Row, Col, ListGroup, Button } from 'react-bootstrap';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue } from 'firebase/database';
import { database } from '../services/firebase';
import Confetti from 'react-confetti';

import './TeacherResultsPage.css';

const TeacherResultsPage = () => {
  const [players, setPlayers] = useState([]);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem('isAdmin');
    if (!isAdmin) {
      navigate('/');
    }

    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (sessionData && sessionData.players) {
        const playerList = Object.values(sessionData.players).sort((a, b) => (b.score || 0) - (a.score || 0));
        setPlayers(playerList);
      }
    });
  }, [gameSessionId]);

  return (
    <Container className="mt-5 teacher-results-page">
      <Confetti />
      <Row className="justify-content-md-center">
        <Col md="8">
          <h1 className="text-center mb-4">Results</h1>
          <div className="podium">
            {players.slice(0, 3).map((player, index) => (
