import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { database } from '../services/firebase';
import { ref, onValue, remove, push, update } from 'firebase/database';
import UploadQuizModal from '../components/UploadQuizModal.jsx';
import EditTimersModal from '../components/EditTimersModal.jsx';

import {
  Container,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Button,
  TextField,
  Grid,
  Card,
  CardContent,
  CardActions,
  InputAdornment
} from '@mui/material';
import { Add, Search, Edit, Delete, PlayArrow } from '@mui/icons-material';

const TeacherLobbyPage = () => {
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [quizzes, setQuizzes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const quizzesRef = ref(database, 'quizzes');
    onValue(quizzesRef, (snapshot) => {
      const data = snapshot.val();
      const quizList = data ? Object.keys(data).map(key => ({ id: key, ...data[key] })) : [];
      setQuizzes(quizList);
    });
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      const quizRef = ref(database, `quizzes/${id}`);
      remove(quizRef);
    }
  };

  const handleEditTimers = (quiz) => {
    setSelectedQuiz(quiz);
    setShowEditModal(true);
  };

  const createMockQuiz = () => {
    const mockQuiz = {
      title: 'Mock Quiz for Testing',
      questions: [
        { text: 'What is 2 + 2?', options: ['3', '4', '5', '6'], correctAnswer: '4' },
        { text: 'What is the capital of France?', options: ['London', 'Berlin', 'Paris', 'Madrid'], correctAnswer: 'Paris' }
      ]
    };
    const quizzesRef = ref(database, 'quizzes');
    push(quizzesRef, mockQuiz);
  };

  const handleStartQuiz = (quizId) => {
    const gameSessionRef = ref(database, 'game_sessions/active_session');
    update(gameSessionRef, {
      quizId: quizId,
      status: 'waiting',
      answers: null,
      currentQuestion: null
    });
    navigate(`/teacher-wait/active_session`);
  };

  const filteredQuizzes = quizzes.filter(quiz =>
    quiz.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Teacher Lobby
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <TextField
            variant="outlined"
            placeholder="Search quizzes..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            sx={{ width: '40%' }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search />
                </InputAdornment>
              ),
            }}
          />
          <Box>
            <Button
              variant="contained"
              startIcon={<Add />}
              onClick={() => setShowUploadModal(true)}
              sx={{ mr: 2 }}
            >
              Upload Quiz
            </Button>
            <Button
              variant="outlined"
              onClick={createMockQuiz}
            >
              Create Mock Quiz
            </Button>
          </Box>
        </Box>
        <Grid container spacing={3}>
          {filteredQuizzes.map((quiz) => (
            <Grid item key={quiz.id} xs={12} sm={6} md={4}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {quiz.title}
                  </Typography>
                  <Typography>
                    {quiz.questions.length} questions
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" startIcon={<PlayArrow />} color="primary" onClick={() => handleStartQuiz(quiz.id)}>
