import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { database } from '../services/firebase';
import { ref, onValue, remove, push, update } from 'firebase/database';
import UploadQuizModal from '../components/UploadQuizModal.jsx';
import EditTimersModal from '../components/EditTimersModal.jsx';

const TeacherLobbyPage = () => {
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [selectedQuiz, setSelectedQuiz] = useState(null);
  const [quizzes, setQuizzes] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const quizzesRef = ref(database, 'quizzes');
    onValue(quizzesRef, (snapshot) => {
      const data = snapshot.val();
      const quizList = data ? Object.keys(data).map(key => ({ id: key, ...data[key] })) : [];
      setQuizzes(quizList);
    });
  }, []);

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this quiz?')) {
      const quizRef = ref(database, `quizzes/${id}`);
      remove(quizRef);
    }
  };

  const handleEditTimers = (quiz) => {
    setSelectedQuiz(quiz);
    setShowEditModal(true);
  };

  const createMockQuiz = () => {
    const mockQuiz = {
      title: 'Mock Quiz for Testing',
      questions: [
        { text: 'What is 2 + 2?', options: ['3', '4', '5', '6'], correctAnswer: '4' },
        { text: 'What is the capital of France?', options: ['London', 'Berlin', 'Paris', 'Madrid'], correctAnswer: 'Paris' }
      ]
    };
    const quizzesRef = ref(database, 'quizzes');
    push(quizzesRef, mockQuiz);
  };

  const handleStartQuiz = (quizId) => {
    const gameSessionRef = ref(database, 'game_sessions/active_session');
    update(gameSessionRef, {
      quizId: quizId,
      status: 'waiting',
      answers: null,
      currentQuestion: null
    });
    navigate(`/teacher-wait/active_session`);
  };

  const filteredQuizzes = quizzes.filter(quiz =>
    quiz.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <div className="navbar bg-base-100 shadow-lg">
        <div className="flex-1">
          <a className="btn btn-ghost text-xl">Teacher Lobby</a>
        </div>
      </div>

      <div className="p-4 md:p-8">
        <div className="flex flex-col md:flex-row justify-between items-center mb-6 gap-4">
          <div className="form-control w-full md:w-1/3">
            <input
              type="text"
              placeholder="Search quizzes..."
              className="input input-bordered"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="flex gap-2">
            <button className="btn btn-primary" onClick={() => setShowUploadModal(true)}>
              Upload Quiz
            </button>
            <button className="btn btn-ghost" onClick={createMockQuiz}>
              Create Mock Quiz
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredQuizzes.map((quiz) => (
            <div key={quiz.id} className="card bg-base-100 shadow-xl">
              <div className="card-body">
                <h2 className="card-title">{quiz.title}</h2>
                <p>{quiz.questions.length} questions</p>
                <div className="card-actions justify-end mt-4">
                  <button className="btn btn-outline btn-sm" onClick={() => handleEditTimers(quiz)}>
                    Edit Timers
                  </button>
                  <button className="btn btn-outline btn-error btn-sm" onClick={() => handleDelete(quiz.id)}>
                    Delete
                  </button>
                  <button className="btn btn-primary btn-sm" onClick={() => handleStartQuiz(quiz.id)}>
                    Start
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Note: These modals will appear unstyled until they are also redesigned. */}
      <UploadQuizModal show={showUploadModal} handleClose={() => setShowUploadModal(false)} />
      {selectedQuiz && <EditTimersModal show={showEditModal} handleClose={() => setShowEditModal(false)} quiz={selectedQuiz} />}
    </div>
  );
};

export default TeacherLobbyPage;

