import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, update } from 'firebase/database';
import { database } from '../services/firebase';
import CustomAvatar from '../components/CustomAvatar.jsx';

import {
  Container,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  CircularProgress
} from '@mui/material';
import { Group, PlayArrow } from '@mui/icons-material';

const TeacherWaitingRoomPage = () => {
  const [players, setPlayers] = useState([]);
  const [quiz, setQuiz] = useState(null);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const isAdmin = localStorage.getItem('isAdmin');
    if (!isAdmin) {
      navigate('/');
      return;
    }

    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    const unsubscribeGameSession = onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();

      if (sessionData?.quizId && !quiz) {
        const quizRef = ref(database, `quizzes/${sessionData.quizId}`);
        get(quizRef).then((quizSnapshot) => {
          if (quizSnapshot.exists()) {
            setQuiz({ id: quizSnapshot.key, ...quizSnapshot.val() });
          }
        });
      }

      if (sessionData?.players) {
        const playerList = Object.keys(sessionData.players).map(key => ({ id: key, ...sessionData.players[key] }));
        setPlayers(playerList);
      } else {
        setPlayers([]);
      }
    });

    return () => {
      unsubscribeGameSession();
    };
  }, [gameSessionId, navigate, quiz]);

  const handleStartQuiz = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'in-progress', currentQuestion: 0 });
    navigate(`/teacher-quiz/${gameSessionId}`);
  };

  if (!quiz) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Waiting Room
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
        <Paper elevation={3} sx={{ p: 3 }}>
          <Typography variant="h4" component="h1" gutterBottom align="center">
            {quiz.title}
          </Typography>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', my: 3 }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Group color="action" sx={{ mr: 1 }} />
              <Typography variant="h6">{players.length} Players Joined</Typography>
            </Box>
            <Button
              variant="contained"
              color="success"
              size="large"
              startIcon={<PlayArrow />}
              onClick={handleStartQuiz}
            >
              Start Quiz
            </Button>
          </Box>
          <List>
            {players.map((player) => (
              <ListItem key={player.id} divider>
                <ListItemAvatar>
                  <CustomAvatar name={player.name} />
                </ListItemAvatar>
                <ListItemText primary={player.name} />
              </ListItem>
            ))}
          </List>
        </Paper>
      </Container>
    </Box>
  );
};

export default TeacherWaitingRoomPage;

