import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, update, remove } from 'firebase/database';
import { database } from '../services/firebase';
import RocketRaceChart from '../components/RocketRaceChart.jsx';
import QuestionRenderer from '../components/QuestionRenderer.jsx';

const TeacherQuizPage = () => {
  const [quiz, setQuiz] = useState(null);
  const [gameSession, setGameSession] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [answers, setAnswers] = useState({});
  const [players, setPlayers] = useState([]);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    const unsubscribeGameSession = onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      setGameSession(sessionData);

      if (sessionData?.players) {
        const playerList = Object.keys(sessionData.players).map(key => ({ id: key, ...sessionData.players[key] }));
        setPlayers(playerList);
      } else {
        setPlayers([]);
      }

      if (sessionData?.quizId && !quiz) {
        const quizRef = ref(database, `quizzes/${sessionData.quizId}`);
        get(quizRef).then((quizSnapshot) => {
          if (quizSnapshot.exists()) {
            const quizData = { id: quizSnapshot.key, ...quizSnapshot.val() };
            setQuiz(quizData);
          }
        });
      }

      if (quiz && sessionData?.currentQuestion !== undefined) {
        setCurrentQuestion(quiz.questions[sessionData.currentQuestion]);
        const answersRef = ref(database, `game_sessions/${gameSessionId}/answers/${sessionData.currentQuestion}`);
        onValue(answersRef, (answersSnapshot) => {
          const answersData = answersSnapshot.val();
          if (answersData) {
            const answerCounts = Object.values(answersData).reduce((acc, { answer }) => {
              acc[answer] = (acc[answer] || 0) + 1;
              return acc;
            }, {});
            setAnswers(answerCounts);
          } else {
            setAnswers({});
          }
        });
      }
    });

    return () => unsubscribeGameSession();
  }, [gameSessionId, quiz]);

  const handleKickPlayer = (playerId) => {
    if (window.confirm('Are you sure you want to kick this player?')) {
      const playerRef = ref(database, `game_sessions/${gameSessionId}/players/${playerId}`);
      remove(playerRef);
    }
  };

  const handleShowResults = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'show_results' });
  };

  const handleNextQuestion = () => {
    if (gameSession && quiz) {
      if (gameSession.currentQuestion < quiz.questions.length - 1) {
        const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
        update(gameSessionRef, { currentQuestion: gameSession.currentQuestion + 1, status: 'in-progress' });
      } else {
        handleEndQuiz();
      }
    }
  };

  const handleEndQuiz = () => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    update(gameSessionRef, { status: 'finished' });
    navigate(`/teacher-results/${gameSessionId}`);
  };

  if (!quiz || !gameSession) {
    return (
      <div className="min-h-screen flex justify-center items-center">
        <span className="loading loading-spinner loading-lg"></span>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen">
      <div className="flex-grow p-4 md:p-6 overflow-y-auto mb-16"> {/* mb-16 for bottom nav */}
        <div className="flex flex-col lg:flex-row gap-6 h-full">

          {/* Left Column: Question */}
          <div className="w-full lg:w-2/3 h-full">
            <div className="card bg-base-100 shadow-xl h-full">
              <div className="card-body">
                <QuestionRenderer question={currentQuestion} answers={answers} />
              </div>
            </div>
          </div>

          {/* Right Column: Meta */}
          <div className="w-full lg:w-1/3 flex flex-col gap-6">
            {currentQuestion?.passage && (
              <div className="card bg-base-100 shadow-xl">
                <div className="card-body">
                  <h2 className="card-title">Passage/Material</h2>
                  <p className="text-sm">{currentQuestion.passage}</p>
                </div>
              </div>
            )}
            <div className="card bg-base-100 shadow-xl flex-grow">
              <div className="card-body">
                <h2 className="card-title mb-4">Rocket Race</h2>
                <RocketRaceChart players={players} maxScore={quiz.questions.length} onKickPlayer={handleKickPlayer} />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Control Bar */}
      <div className="btm-nav">
        <button className="disabled">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 9v6m4-6v6m7-3a9 9 0 11-18 0 9 9 0 0118 0z" /></svg>
          <span className="btm-nav-label">Pause</span>
        </button>
        <button className="bg-info text-info-content" onClick={handleShowResults}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" /></svg>
          <span className="btm-nav-label">Results</span>
        </button>
        <button className="bg-secondary text-secondary-content" onClick={handleNextQuestion}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 5l7 7-7 7M5 5l7 7-7 7" /></svg>
          <span className="btm-nav-label">Next</span>
        </button>
        <button className="bg-error text-error-content" onClick={handleEndQuiz}>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z" /><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 10h.01M15 10h.01M9 14h6" /></svg>
          <span className="btm-nav-label">End</span>
        </button>
      </div>
    </div>
  );
};

export default TeacherQuizPage;

