import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue, get, push, update } from 'firebase/database';
import { database } from '../services/firebase';
import AnswerInputRenderer from '../components/AnswerInputRenderer.jsx';

const StudentQuizPage = () => {
  const [quiz, setQuiz] = useState(null);
  const [currentQuestion, setCurrentQuestion] = useState(null);
  const [showFeedback, setShowFeedback] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);
  const [playerScore, setPlayerScore] = useState(0);
  const [hasAnswered, setHasAnswered] = useState(false);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);

    const unsubscribe = onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (!sessionData) return;

      if (sessionData.status === 'finished') {
        navigate(`/student-results/${gameSessionId}`);
      }

      if (sessionData.quizId && !quiz) {
        get(ref(database, `quizzes/${sessionData.quizId}`)).then((quizSnapshot) => {
          if (quizSnapshot.exists()) setQuiz({ id: quizSnapshot.key, ...quizSnapshot.val() });
        });
      }

      if (quiz && sessionData.currentQuestion !== undefined) {
        setCurrentQuestion(quiz.questions[sessionData.currentQuestion]);
        setHasAnswered(false);
        setShowFeedback(false);
      }

      if (sessionData.status === 'show_results' && quiz) {
        const playerId = localStorage.getItem('playerId');
        const player = sessionData.players?.[playerId];
        const playerAnswerData = sessionData.answers?.[sessionData.currentQuestion];
        const playerAnswer = playerAnswerData ? Object.values(playerAnswerData).find(a => a.playerId === playerId) : null;
        const question = quiz.questions[sessionData.currentQuestion];

        const correct = playerAnswer?.answer === question.correctAnswer;
        setIsCorrect(correct);

        if (correct && player && !player.scoreUpdatedFor?.[sessionData.currentQuestion]) {
          const newScore = (player.score || 0) + 1;
          const updates = {};
          updates[`/game_sessions/${gameSessionId}/players/${playerId}/score`] = newScore;
          updates[`/game_sessions/${gameSessionId}/players/${playerId}/scoreUpdatedFor/${sessionData.currentQuestion}`] = true;
          update(ref(database), updates);
          setPlayerScore(newScore);
        } else if (player) {
          setPlayerScore(player.score || 0);
        }
        setShowFeedback(true);
      }
    });

    return () => unsubscribe();
  }, [gameSessionId, navigate, quiz]);

  const handleAnswerSubmit = (answer) => {
    if (hasAnswered) return;
    setHasAnswered(true);
    const playerId = localStorage.getItem('playerId');
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    get(gameSessionRef).then(snap => {
        const currentQuestionIndex = snap.val().currentQuestion;
        const answersRef = ref(database, `game_sessions/${gameSessionId}/answers/${currentQuestionIndex}`);
        push(answersRef, { answer, playerId });
    });
  };

  const renderFeedback = () => {
    const question = quiz.questions[gameSession.currentQuestion];
    const bgColor = isCorrect ? 'bg-success text-success-content' : 'bg-error text-error-content';

    return (
      <div className={`card w-full max-w-md shadow-2xl ${bgColor}`}>
        <div className="card-body items-center text-center p-10">
          <h2 className="card-title text-5xl font-bold">
            {isCorrect ? 'Correct!' : 'Incorrect'}
          </h2>
          {!isCorrect && (
            <p className="text-lg mt-2">Correct Answer: {question.correctAnswer}</p>
          )}
          <p className="text-2xl mt-4">Your Score: {playerScore}</p>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen flex flex-col justify-center items-center p-4 bg-base-200">
      {!currentQuestion ? (
        <span className="loading loading-spinner loading-lg"></span>
      ) : showFeedback ? (
        renderFeedback()
      ) : (
        <div className="w-full max-w-xl">
          <AnswerInputRenderer question={currentQuestion} onAnswerSubmit={handleAnswerSubmit} />
          {hasAnswered && <p className="text-center mt-4">Answer submitted! Waiting for next question...</p>}
        </div>
      )}
    </div>
  );
};

export default StudentQuizPage;

