import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue } from 'firebase/database';
import { database } from '../services/firebase';
import Confetti from 'react-confetti';
import {
  Container,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar as MuiAvatar,
  Divider
} from '@mui/material';
import { EmojiEvents } from '@mui/icons-material';

const podiumStyles = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'flex-end',
  gap: 2,
  mb: 4,
  minHeight: 250,
};

const podiumPlace = (place, player) => {
  const heights = { 1: 200, 2: 150, 3: 100 };
  const colors = { 1: '#FFD700', 2: '#C0C0C0', 3: '#CD7F32' };
  return (
    <Box sx={{ textAlign: 'center' }}>
      <EmojiEvents sx={{ fontSize: 40, color: colors[place] }} />
      <Paper
        elevation={6}
        sx={{
          height: heights[place],
          width: 100,
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          p: 1,
        }}
      >
        <Typography variant="h6">{player.name}</Typography>
        <Typography variant="h5">{player.score || 0}</Typography>
      </Paper>
    </Box>
  );
};

const TeacherResultsPage = () => {
  const [players, setPlayers] = useState([]);
  const navigate = useNavigate();
  const { gameSessionId } = useParams();

  useEffect(() => {
    const isAdmin = localStorage.getItem('isAdmin');
    if (!isAdmin) {
      navigate('/');
    }

    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (sessionData && sessionData.players) {
        const playerList = Object.values(sessionData.players).sort((a, b) => (b.score || 0) - (a.score || 0));
        setPlayers(playerList);
      }
    });
  }, [gameSessionId, navigate]);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Confetti />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Final Results
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
        <Paper elevation={3} sx={{ p: 3 }}>
          <Box sx={podiumStyles}>
            {players[1] && podiumPlace(2, players[1])}
            {players[0] && podiumPlace(1, players[0])}
            {players[2] && podiumPlace(3, players[2])}
          </Box>
          <Divider sx={{ my: 2 }} />
          <Typography variant="h5" gutterBottom>Leaderboard</Typography>
          <List>
            {players.map((player, index) => (
              <ListItem key={index}>
                <ListItemAvatar>
                  <MuiAvatar>{index + 1}</MuiAvatar>
                </ListItemAvatar>
                <ListItemText primary={player.name} secondary={`Score: ${player.score || 0}`} />
              </ListItem>
            ))}
          </List>
          <Box sx={{ textAlign: 'center', mt: 4 }}>
            <Button variant="contained" onClick={() => navigate('/lobby')}>
              Return to Lobby
            </Button>
          </Box>
        </Paper>
      </Container>
    </Box>
  );
};

export default TeacherResultsPage;

