import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue } from 'firebase/database';
import { database } from '../services/firebase';
import {
  Container,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemAvatar,
  Avatar as MuiAvatar,
  Divider
} from '@mui/material';

const StudentResultsPage = () => {
  const [players, setPlayers] = useState([]);
  const [player, setPlayer] = useState(null);
  const [rank, setRank] = useState(0);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (sessionData && sessionData.players) {
        const playerList = Object.entries(sessionData.players)
          .map(([id, data]) => ({ id, ...data }))
          .sort((a, b) => (b.score || 0) - (a.score || 0));
        setPlayers(playerList);

        const playerId = localStorage.getItem('playerId');
        const currentPlayer = playerList.find(p => p.id === playerId);
        if (currentPlayer) {
          setPlayer(currentPlayer);
          setRank(playerList.findIndex(p => p.id === playerId) + 1);
        }
      }
    });
  }, [gameSessionId]);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Final Results
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="sm" sx={{ mt: 4, mb: 4 }}>
        {player && (
          <Paper elevation={3} sx={{ p: 3, mb: 3, textAlign: 'center' }}>
            <Typography variant="h4" component="h1" gutterBottom>
              Your Results
            </Typography>
            <Box sx={{ display: 'flex', justifyContent: 'space-around', alignItems: 'center' }}>
              <Box>
                <Typography variant="h6" color="text.secondary">Score</Typography>
                <Typography variant="h3" component="p">{player.score || 0}</Typography>
              </Box>
              <Box>
                <Typography variant="h6" color="text.secondary">Rank</Typography>
                <Typography variant="h3" component="p">{rank}</Typography>
              </Box>
            </Box>
          </Paper>
        )}
        <Paper elevation={3} sx={{ p: 2 }}>
          <Typography variant="h5" gutterBottom sx={{ pl: 2 }}>Top 5 Players</Typography>
          <List>
            {players.slice(0, 5).map((p, index) => (
              <ListItem key={p.id}>
                <ListItemAvatar>
                  <MuiAvatar>{index + 1}</MuiAvatar>
                </ListItemAvatar>
                <ListItemText primary={p.name} secondary={`Score: ${p.score || 0}`} />
              </ListItem>
            ))}
          </List>
        </Paper>
        <Box sx={{ textAlign: 'center', mt: 4 }}>
          <Button variant="contained" size="large" onClick={() => navigate('/')}>
            Play Again
          </Button>
        </Box>
      </Container>
    </Box>
  );
};

export default StudentResultsPage;

