import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ref, onValue } from 'firebase/database';
import { database } from '../services/firebase';

const StudentResultsPage = () => {
  const [players, setPlayers] = useState([]);
  const [player, setPlayer] = useState(null);
  const [rank, setRank] = useState(0);
  const { gameSessionId } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const gameSessionRef = ref(database, `game_sessions/${gameSessionId}`);
    onValue(gameSessionRef, (snapshot) => {
      const sessionData = snapshot.val();
      if (sessionData && sessionData.players) {
        const playerList = Object.entries(sessionData.players)
          .map(([id, data]) => ({ id, ...data }))
          .sort((a, b) => (b.score || 0) - (a.score || 0));
        setPlayers(playerList);

        const playerId = localStorage.getItem('playerId');
        const currentPlayer = playerList.find(p => p.id === playerId);
        if (currentPlayer) {
          setPlayer(currentPlayer);
          setRank(playerList.findIndex(p => p.id === playerId) + 1);
        }
      }
    });
  }, [gameSessionId]);

  return (
    <div>
      <div className="navbar bg-base-100 shadow-lg">
        <div className="flex-1">
          <a className="btn btn-ghost text-xl">Final Results</a>
        </div>
      </div>

      <div className="p-4 md:p-8 flex flex-col items-center gap-8">
        {player && (
          <div className="stats shadow">
            <div className="stat">
              <div className="stat-title">Your Score</div>
              <div className="stat-value">{player.score || 0}</div>
            </div>
            <div className="stat">
              <div className="stat-title">Your Rank</div>
              <div className="stat-value">{rank}</div>
              <div className="stat-desc">out of {players.length} players</div>
            </div>
          </div>
        )}

        <div className="card w-full max-w-2xl bg-base-100 shadow-xl">
          <div className="card-body">
            <h2 className="card-title">Top 5 Players</h2>
            <div className="overflow-x-auto">
              <table className="table w-full">
                <thead>
                  <tr>
                    <th>Rank</th>
                    <th>Name</th>
                    <th>Score</th>
                  </tr>
                </thead>
                <tbody>
                  {players.slice(0, 5).map((p, index) => (
                    <tr key={p.id} className="hover">
                      <th>{index + 1}</th>
                      <td>{p.name}</td>
                      <td>{p.score || 0}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        <div className="text-center mt-4">
          <button className="btn btn-primary btn-lg" onClick={() => navigate('/')}>
            Play Again
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentResultsPage;

